import { TypedDispatch } from "..";
import getNews from "../../services/getNews";

import { IStore } from "./reduser"; 



export const setMoviesMainPage = (list: IStore['list']) => {
  return {
    type: 'films/setFilms',
    payload: list,
  }
};

export const loadMovisMainPage = () => async (dispatch: TypedDispatch ) => {
  try {
    const response = await getNews();
    dispatch(setMoviesMainPage(response.sh));
  } catch (e) {
    console.log(e, "у нас проблемы!!!!");
  }
};
