import React, { useState, useEffect } from "react";
import routeDeteil from "./routes";

import getOneNews from "services/getOneNews";

import { useParams } from "react-router-dom";
import { ID } from "../../types/ID";
import star from "../../pictures/star.png";
import { IDeteil } from "types/IDeteil";

import "./styles.scss";

const DeteilPage = () => {
  const { id } = useParams<ID>();
  const [news, setNews] = useState<IDeteil | undefined>(undefined);

  useEffect(() => {
    getOneNews(id).then((response) => {
      setNews(response.data);
    });
  }, [id]);
  return (
    <section className="newsPostItem">
      {news ? (
        <div className="newsDeteilWrapper">
          <img
            className="imgdet"
            src={news.image.medium}
            alt={news.image.medium}
          />
          <div className="starrat"></div>
             <div className="rating">{news.rating.average}</div>
             <img className="imgstar" src={star} alt={star} />
<div className="wrapDeteilPage">
          <div className="namefilm">{news.name}</div>
          <div className="prem"><h3>Год выхода:</h3>  {news.premiered}</div>
          <div className="country">
           <h3> Страна: </h3>  {" "}
                  {news.network.country.name}
          </div>

          <div className="genres"><h3> Жанр:</h3> {news.genres} </div>
          <div className="summary"><h3>Описание:</h3> {news.summary}</div>
          <div className="language">Язык: {news.language}</div>

               </div>
        </div>
      ) : (
        <>
          {" "}
          <h2>ERROR</h2>{" "}
        </>
      )}
    </section>
  );
};
export { routeDeteil };

export default DeteilPage;
