import { IStore } from "./type";

import { AnyAction } from "redux";

const initialState = {
  list: [],
};

const movieReducer = (state: INews = initialState, action: AnyAction) => {
  switch (action.type) {
    case "films/setFilms":
      return { ...state, list: [...action.payload] };
    default:
      return state;
  }
};
export default movieReducer;
