import React, { useState, useEffect } from "react";
import routeDeteil from "./routes";

import { useSelector } from "react-redux";
import { selectFilms } from "../../store/FirstFilm/selector";
import { useParams } from "react-router-dom";
import { ID } from "../../types/ID";

import { INews } from "../../types/INews";

import "./styles.scss";

const DeteilPage = () => {
  const { id } = useParams<ID>();
  const [news, setNews] = useState<INews | undefined>(undefined);

  const newFilms = useSelector(selectFilms);

  useEffect(() => {
    const currentFilms = newFilms?.find((item: INews) => item.show.id == id);
    setNews(currentFilms);
  }, [id, newFilms]);
  return (
    <section className="newsPostItem">
      {news ? (
        <div className="newsDeteilWrapper">
          <div className="img">
            <img src={news.show.image.medium} alt={news.show.image.medium} />
            <div className="namefilm">{news.show.name}</div>
          </div>
          <div className="summary">
            <h3>Описание фильма:</h3> {news.show.summary}
          </div>

          <div className="genres">
            {" "}
            <h3>Жанры:</h3>
            {news.show.genres}{" "}
          </div>
          <div className="language">
            <h3>Язык:</h3>
            {news.show.language}
          </div>
          <div className="country">
            <h3>Страна:</h3>
            {news.show.network.country.name}
          </div>
          <div className="rating">
            <h3>Рейтинг:</h3>
            {news.show.rating.average}
          </div>
          <div className="genres">
            <h3>Год выпуска:</h3>
            {news.show.premiered}
          </div>
        </div>
      ) : (
        <> Ybxtuj yt gjkexbkjcm!!! </>
      )}
    </section>
  );
};
export { routeDeteil };

export default DeteilPage;
