import React, { useState, useEffect } from "react";
import routeSearch from "./routes";
import getNewCategory from "services/getNewCategory";
import { ICategory } from "types/ICategory";
import { useParams } from "react-router-dom";
import { IDeteil } from "types/IDeteil";
import "./styles.scss";

import { useForm } from "react-hook-form";
import { SubmitHandler } from "react-hook-form/dist/types";

const SearchPage = () => {
  const { register, handleSubmit } = useForm<IDeteil>();
  const { category } = useParams<ICategory>();

  const onSubmit: SubmitHandler<IDeteil> = (ansver) => {
    getNewCategory(ansver.genres);
    setNews(genres);
  };

  const [genres, setNews] = useState<IDeteil | undefined>(undefined);

  useEffect(() => {
    getNewCategory(category).then((response) => {
      setNews(response.data);
    });
  }, []);
  console.log(genres);
  console.log(response.data);
  return (
    <div className="wrapsearch">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="clearfon">
          <h1>Bведите запрос.</h1>

          <input
            {...register("genres")}
            type="text"
            placeholder="example: animals"
          />
          <button>Send</button>
        </div>
      </form>
      <div className="newcategory">{}</div>
    </div>
  );
};
export { routeSearch };
export default SearchPage;
