import React, { useState, useEffect } from "react";
import routeDeteil from "./routes";

import getOneNews from "../../services/getOneNews";
import { useParams } from "react-router-dom";



import "./styles.scss";

const DeteilPage = () => {
    const { id } = useParams();
  const [news, setNews] = useState(null);
  console.log(id)
  useEffect(() => {
    getOneNews(id).then((response) => {
      setNews(response.data.show.find(item => item.id === id));
      console.log(response.data)
    });
  }, []);
  return (
    <section className="newsPostItem">
           {news ? (
        <div className="newsDeteilWrapper">
          <div className="newsTitle">
            <h2 className="title">{news.show.name}</h2>
          </div>
          <div className="newsBody">
            <h2 className="Body">{news.body}</h2>
          </div>
        </div>
      ) : (
        <></>
      )}

    </section>
  );
};
export{routeDeteil}

export default DeteilPage;
