import React, { useState, useEffect } from "react";
import routeDeteil from "./routes";


import getOneNews from "services/getOneNews";

import { useParams } from "react-router-dom";
import { ID } from "../../types/ID";
import star from "../../pictures/star.png"
import { INews } from "../../types/INews";

import "./styles.scss";

const DeteilPage = () => {
  const { id } = useParams<ID>();
  const [news, setNews] = useState<INews | undefined>(undefined);

  useEffect(() => {
    getOneNews(id).then((response) => {
      console.log(response)
      console.log(response.data)
      setNews(response.data);
      console.log(response.data)
    });
  }, [id]);
  return (
    <section className="newsPostItem">
      {news ? (
        <div className="newsDeteilWrapper">
         
          <div className="language">
           Язык: {news.show.language}
          </div>
          <img
            className="imgstar"
            src={star}
            alt={star}
          />
          <div className="rating">
            {news.show.rating.average}
          </div>
        </div>
      ) : (
        <> Ybxtuj yt gjkexbkjcm!!! </>
      )}
    </section>
  );
};
export { routeDeteil };

export default DeteilPage;
