import { TypedDispatch } from "..";
import getOneNews from "services/getOneNews";

import { IStoreD } from "./reducer";

export const setMoviesDeteilPage = (payload: IStoreD["list"]) => {
  return {
    type: "films/setFilms",
    payload
  };
};

export function loadMovisDeteilPage({ id }: { id: string; }) {
  return async (dispatch: TypedDispatch) => {
    try {
      const response = await getOneNews(id);
      console.log(id);
      dispatch(setMoviesDeteilPage(response.data));

    } catch (e) {
      console.log(e, "у нас проблемы!!!!");
    }
  };
}
