import { TypedDispatch } from "..";
import getNewCategor from "../../services/getNewCateg";

import { IStore } from "./reduser";

export const setNewsActionsSecond = (payload: IStore["list"]) => {
  return {
    type: "films/setFilms",
    payload: list,
  };
};

export const loadFilmsSecond = () => async (dispatch: TypedDispatch) => {
  try {
    const response = await getNewCategor();
    dispatch(setNewsActionsSecond(response.data));
  } catch (e) {
    console.log(e, "у нас проблемы!!!!");
  }
};
