import  { useEffect } from "react";
import routeSearch from "./routes";
import { getSearchFilms } from "store/searchpage/selector";
import { useDispatch, useSelector } from "react-redux";
import { TypedDispatch } from "../../store";
import { ICategory } from "types/ICategory";
import { useParams } from "react-router-dom";
import { IDeteil } from "types/IDeteil";

import { loadSearchPage } from "store/searchpage/actions";
import { useForm } from "react-hook-form";
import { SubmitHandler } from "react-hook-form/dist/types";
import star from "../../pictures/star.png";
import { INews } from "types/INews";
import "./styles.scss";



const SearchPage = () => {
  const { register, handleSubmit } = useForm<INews>();
  const { category } = useParams<ICategory>();
  const dispatch = useDispatch<TypedDispatch>();
  const news = useSelector(getSearchFilms);

  const onSubmit: SubmitHandler<INews> = (category) => {
    dispatch(loadSearchPage(category.show.genres));
    console.log(category.show.genres)
  };

  useEffect(() => {
    dispatch(loadSearchPage(category));
  }, [dispatch]);

 console.log(news.show)

  return (
    <div className="wrapsearch">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="clearfon">
          <h1>I wait you</h1>
             <input
            {...register("show.genres")}
            type="text"
            placeholder="example: animals"
          />
          <button>Send</button>
          <div className="language">Язык: {news.show.language}{"FFFFFFFFFFFFFFFFFFF"}</div>
        </div>
      </form>
      <div className="newcategory">{}</div>
    </div>
  );
};
export { routeSearch };
export default SearchPage;
