import getOneNews from "services/getOneNews";
import { TypedDispatch } from "store";

import { IStore } from "./reducer";


export const setMoviesDeteilPage = (payload: IStore["movie"]) => {
  console.log(payload)
  return {
    type: "cinema/setCinema",
    payload
  };
  
};

export const loadMovisDeteilPage = (id: string) => async (dispatch: TypedDispatch) => {
    try {
        const response = await getOneNews(id);
    console.log(response.data)
    dispatch(setMoviesDeteilPage((response.statusText)));
    console.log(response.data)
  } catch (e) {
    console.log(e, "у нас проблемы!!!!");
  }
};
