import { INews } from "types/INews";
import { AnyAction } from "redux";



const movieReducer = (state: INews , action: AnyAction) => {
  switch (action.type) {
    case "films/setFilms":
      return { ...state, list: [...action.payload] };
    default:
      return state;
  }
};
export default movieReducer;
