import { loadMovisDeteilPage } from "store/deteilpage/actions";
import { getDeteilFilms } from "store/deteilpage/selector"; 
import { TypedDispatch } from "../../store";
import star from "../../pictures/star.png";
import { INews } from "types/INews";

import { ID } from "types/ID";


import "./styles.scss";
interface InewsItemParams {
  news: INews;
}



const DeteilPage = () => {
  const { id } = useParams<ID>();
  const dispatch = useDispatch<TypedDispatch> ();
  const NewsItem: React.FC<InewsItemParams> = ({ news }) => useSelector(getDeteilFilms);
  console.log(getDeteilFilms)
console.log(news)
console.log(id)

  useEffect(() => {
    dispatch(loadMovisDeteilPage(id));
    console.log(dispatch)
  }, [dispatch]);
  console.log(loadMovisDeteilPage(id))
  return (
    <section className="newsPostItem">
      { news ? (
        <div className="newsDeteilWrapper">
      
        </div>
      ) : (
        <>
          {" "}
          <h2>ERROR</h2>{" "}
        </>
      )}
    </section>
  );
};
export { routeDeteil };

export default DeteilPage;


