import { Dispatch } from "redux";

import getNews from "../../services/getNews";

import { IStore } from "./type";



export const setNewsActions = (list: IStore['list']) => {
  return {
    type: 'films/setFilms',
    payload: list,
  }
};

export const loadFilms = () => async (dispatch: Dispatch<Action>  ) => {
  try {
    const response = await getNews();
    dispatch(setNewsActions(response.data));
  } catch (e) {
    console.log(e, "у нас проблемы!!!!");
  }
};
