import { AnyAction } from "redux";
import { INews } from "types/INews";



export interface IStore {
    list:INews[];
}


const movieReducer = ( state: IStore , action: AnyAction) => {
 switch(action.type) {
    case 'films/setFilms':
        return {...state, list:[...action.payload]}
        default :
        return state;
 }
}
export default movieReducer;
