import React, { useEffect } from "react";
import routeSearch from "./routes";
import { getSearchFilms } from "store/searchpage/selector";
import { useDispatch, useSelector } from "react-redux";
import { TypedDispatch } from "../../store";
import { ICategory } from "types/ICategory";
import { useParams } from "react-router-dom";
import { IDeteil } from "types/IDeteil";
import { INews } from "types/INews";
import { loadSearchPage } from "store/searchpage/actions";
import { useForm } from "react-hook-form";
import { SubmitHandler } from "react-hook-form/dist/types";
import star from "../../pictures/star.png";
import "./styles.scss";



const SearchPage = () => {
  const { register, handleSubmit } = useForm<INews>();

  const dispatch = useDispatch<TypedDispatch>();
  const news = useSelector(getSearchFilms);

  const onSubmit: SubmitHandler<INews> = (category) => {
    dispatch(loadSearchPage(category.show.genres));
    console.log(category.show.genres)

  };

  useEffect(() => {
    dispatch(loadSearchPage(category));
  }, [dispatch]);

 console.log(news)


  return (
    <div className="wrapsearch">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="clearfon">
          <h1>I wait you</h1>
             <input
            {...register('show.genres')}
            type="text"
            placeholder="example: animals"
          />
          <button>Send</button>
          <section className="newsPostItem">
            {news ? (
              <div className="newsDeteilWrapper">
                <img
                  className="imgdet"
                  src={news.image.medium}
                  alt={news.image.medium}
                />
                <div className="starrat">
                  <img className="imgstar" src={star} alt={star} />
                  <div className="rating">{news.rating.average}</div>
                </div>
                <div className="wrapDeteilPage">
                  <div className="namefilm">{news.name}</div>
                  <div className="prem">
                    <h3 className="stylyname">Год выхода:</h3> {news.premiered}
                  </div>
                  <div className="country">
                    <h3 className="stylyname"> Страна: </h3>{" "}
                    {news.network.country.name}
                  </div>

                  <div className="genres">
                    <h3 className="stylyname"> Жанр:</h3> {news.genres}{" "}
                  </div>
                  <div className="summary">
                    <h3 className="stylyname">Описание:</h3> {news.summary}
                  </div>
                  <div className="language">Язык: {news.language}{"FFFFFFFFFFFFFFFFFFF"}</div>
                </div>
              </div>
            ) : (
              <>
                {" "}
                <h2>ERROR</h2>{" "}
              </>
            )}
          </section>
        </div>
      </form>
      <div className="newcategory">{}</div>
    </div>
  );
};
export { routeSearch };
export default SearchPage;
