import React, { useState, useEffect } from "react";
import routeDeteil from "./routes";

import getOneNews from "../../services/getOneNews";
import { useParams } from "react-router-dom";

import "./styles.scss";

const DeteilPage = () => {
  const { id } = useParams();
  const [news, setNews] = useState(null);
  console.log(id);
  useEffect(() => {
    getOneNews(id).then((response) => {
      setNews(response.data);
      console.log(response.data);
    });
  }, []);
  return (
    <section className="newsPostItem">
      {news ? (
        <div className="newsDeteilWrapper">
        <div className="img">
          <img src={news.image.medium} alt={news.image.medium} />
        </div>
          <div className="summary">
            <h3 className="Body">{news.summary}</h3>
            <div className="genres">{news.genres}</div>
          </div>
        </div>
      ) : (
        <></>
      )}
    </section>
  );
};
export { routeDeteil };

export default DeteilPage;
