import React from "react";
import { NavLink } from "react-router-dom";

import "./styles.scss";



const NewsItem = ({ item }) => (
<div>
<div className="title">
      <p>картинка</p>
      <img src={item.image.medium} alt={item.image.medium}/>
      </div>
    
    <div className="title">
      <p>Hазвание фильма</p>
      {item.name}
    </div>
    <div className="bottonwrapper">
      <div className="body">
        <p>жанр</p>
        {item.sho.genres}
      </div>
      <div className="body">
        <p>страна фильма</p>

      </div>
    </div>
    </div>
);
export default NewsItem;
