          <div className="summary">
            Описание: {news.show.summary}
          </div>
          <div className="language">
           Язык: {news.show.language}
          </div>
          <img
            className="imgstar"
            src={star}
            alt={star}
          />
          <div className="rating">
            {news.show.rating.average}
          </div>
        </div>
      ) : (
        <> Ybxtuj yt gjkexbkjcm!!! </>
      )}
    </section>
  );
};
export { routeDeteil };

export default DeteilPage;
