    useEffect(() => {
        getNews().then((response) => {
        setNewsList(response.data);
        console.log(response.data)

      });
    }, []);
    return(
        <div className="secondpage">
            <div className="secondtext">Выбранная категория: <span> CAR</span>
          
            </div>
            <div className="SecondPage">
      {newsList.length > 0 && <SeconNI list={newsList.slice(0, 20)} />}
      </div>
        </div>
    )
}
