import { TypedDispatch } from "..";
import getOneNews from "services/getOneNews";

import { IStore } from "./reducer";
import { Dispatch } from "redux";

export const setMoviesDeteilPage = (payload: IStore["movie"]) => {
  console.log(payload)
  return {
    type: "/setFilm",
    payload
  };
  
};

export const loadMovisDeteilPage = (id: string) => async (dispatch: Dispatch) => {

  try {
    const response = await getOneNews(id);
    dispatch(setMoviesDeteilPage((response.data)));
    console.log(response.data)
  } catch (e) {
    console.log(e, "у нас проблемы!!!!");
  }
};
