import { IStore } from "./type";
import { AnyAction } from "@reduxjs/toolkit";

const initialState = {
    list: [],
}

const newReduser = ( state: IStore = initialState, action: AnyAction) => {
 switch(action.type) {
    case 'films/setFilms':
        return {...state, list:[...action.payload]}
 }
}
export default newReduser;
